import { useState } from "react";
import { Avatar } from "./Avatar";

type Task = {
    id: number;
    title: string;
    assignee: string;
}

const member = {
    a: 'A',
    b: 'B',
    c: 'C',
}

const generateDummyTasks = (): Task[] => {
    return Array(10000).fill('').map((_, index) => {
        const addedIndex = index + 1;
        return {
            id: addedIndex,
            title: `タスク${addedIndex}`,
            assignee: addedIndex % 3 === 0 ? member.a :addedIndex % 2 === 0 ? member.b : member.c
        }
    })
}

const tasks = generateDummyTasks();

export const Transition = () => {
    const [selectedAssignee, setSelectedAssignee] = useState<string>('');
    const [tasksList, setTasksList] = useState<Task[]>(tasks);

    const onClickAssignee = (assignee: string) => {
        // alert(assignee);
        setSelectedAssignee(assignee);
    }

    return (
        <div>
            <p>Transition</p>
            <div style={{ display: 'flex' , justifyContent: 'center' }}>
                <Avatar onClick={onClickAssignee} >{member.a}</Avatar>
                <Avatar onClick={onClickAssignee} >{member.b}</Avatar>
                <Avatar onClick={onClickAssignee} >{member.c}</Avatar>
            </div>
            {tasksList.map((task) => (
                <div key={task.id} style={{ width: '300px' , margin: 'auto', backgroundColor: 'lavender'}}>
                    <p>タイトル：{task.title}</p>
                    <p>担当：{task.assignee}</p>
                </div>
            ))}
        </div>
    )
}
